mapper.select:
SELECT IF(COUNT(1) = 1, true, false)
FROM USERS
WHERE EMP_NO = #{empNo}

SELECT U.EMP_NO, U.DEPT_CODE, U.USER_ID, U.USER_PW, U.NAME, U.POSITION, U.EMAIL
FROM USERS U
WHERE NAME = #{name}

mapper.insert:
INSERT INTO USERS
(
EMP_NO, DEPT_CODE
, USER_PW, NAME
, ACCOUNT_TYPE, POSITION
,EMAIL, PHONE
, USE_YN, EMPLOYED_AT
, RESIGNED_AT, CREATED_BY
, CREATED_AT, UPDATED_BY
, UPDATED_AT
)
VALUES(
#{empNo}, #{deptCode}
, #{userPw}, #{name}
, #{accountType}, #{position}
, #{email}, #{phone}
,'Y', #{employedAt}
, #{resignedAt}, 'admin'
, sysdate(),'admin'
, sysdate()
)

mapper.update:
UPDATE USERS U
SET USER_PW = #{password}
WHERE U.ADMIN_ID = #{userId}
    OR U.EMP_NO = #{userId}
