mapper.select:
SELECT IF(COUNT(1) = 1, true, false)
FROM USERS
WHERE EMP_NO = #{empNo}

SELECT U.EMP_NO, U.DEPT_CODE, U.USER_ID, U.USER_PW, U.NAME, U.POSITION, U.EMAIL
FROM USERS U
WHERE NAME = #{name}

mapper.insert:
INSERT INTO USERS
(
EMP_NO, DEPT_CODE
, USER_PW, NAME
, ACCOUNT_TYPE, POSITION
,EMAIL, PHONE
, USE_YN, EMPLOYED_AT
, RESIGNED_AT, CREATED_BY
, CREATED_AT
)
VALUES(
#{empNo}, #{deptCode}
, #{userPw}, #{name}
, #{accountType}, #{position}
, #{email}, #{phone}
,'Y', #{employedAtStr}
, #{resignedAtStr}, 'admin'
, sysdate()
)

mapper.update:
UPDATE USERS U
SET USER_PW = #{userPw}, UPDATED_AT = sysdate(), UPDATED_BY = #{userId}
WHERE U.EMP_NO = #{empNo}

UPDATE USERS U
SET U.USER_PW = #{userPw}, U.UPDATED_AT = sysdate() , U.UPDATED_BY = #{empNo}
WHERE U.EMP_NO = #{empNo}

UPDATE USERS U
SET
    U.UPDATED_AT = sysdate() ,
    U.UPDATED_BY = #{adminId}

        USER_PW = #{userPw} ,


        NAME = #{name}

WHERE
    U.EMP_NO = #{adminId}

UPDATE USERS U
    SET U.UPDATED_AT = sysdate() ,
         U.authority = #{authority}
WHERE U.EMP_NO = #{empNo}

UPDATE USERS
    SET authority = NULL
WHERE EMP_NO = #{userId}
