SELECT TEMPLATE_SEQ
FROM CONTRACT_TEMPLATE
WHERE TEMPLATE_TYPE = #{processStatus}

SELECT CT.TEMPLATE_TITLE AS TEMPLATE_TITLE, C.CONTRACT_NO AS CONTRACT_NO, C.EMP_NO AS EMP_NO, C.TEMPLATE_SEQ AS TEMPLATE_SEQ, C.PROCESS_STATUS AS PROCESS_STATUS, C.DOC_STATUS AS DOC_STATUS, C.NAME AS NAME , C.CREATED_AT AS CREATED_AT, C.SIGN_DATE AS SIGN_DATE , CTD.CONTRACT_TITLE_EN AS CONTRACT_TITLE_EN FROM CONTRACTS C INNER JOIN CONTRACT_TEMPLATE CT ON C.TEMPLATE_SEQ = CT.TEMPLATE_SEQ INNER JOIN CONTRACT_TEMPLATE_DETAILS CTD ON C.TEMPLATE_SEQ = CTD.TEMPLATE_SEQ WHERE DOC_STATUS IN
#{item}
LIMIT #{paging.displayRow} OFFSET #{paging.offset}
