mapper.select:
SELECT c.contract_no /*getContractProgressList*/ , c.emp_no , c.template_seq , c.doc_status , c.process_status , c.signature_data_no , c.validation , c.agree_yn , c.del_yn , c.created_by , c.created_at , c.updated_by , c.updated_at , ct.template_title , c.name , ctd.contract_title_en , c.dept_code , c.sign_date , date_format(c.assigned_at ,'%Y-%m-%d') as assigned_at , c.assigned_by from contracts c join contract_template ct on c.template_seq = ct.template_seq join contract_template_details ctd on ct.template_seq = ctd.template_seq where c.del_yn = 'N'
order by c.created_at desc
LIMIT #{paging.displayRow} OFFSET #{paging.offset}

if.foreach:
update contracts
   set doc_status = #{item.docStatus}
 where contract_no = #{item.contractNo}

update.if:
update contracts
   set doc_status = #{docStatus}
     , updated_by = #{updatedBy}
     , updated_at = now()
     , assigned_at = now()
     , assigned_by = #{updatedBy}
 where contract_no = #{contractNo}

mapper.update:
update contracts
set process_status = 'PRCS2002'
where contract_no = #{contractNo}
